import React, { useState } from "react";
import TaskForm from "./components/TaskForm/TaskForm";
import AllTasks from "./components/Tasklist/AllTasks";
import ClearList from "./components/ClearList/ClearList";

const DUMMY_DATA = [
  { title: "Feed The dog" },
  { title: "Buy the groceries" },
  { title: "Fuck me daddy" },
  { title: "Make dinner" },
];

const App = () => {
  const tasks = [
    { title: "Feed The dog" },
    { title: "Buy the groceries" },
    { title: "Fuck me daddy" },
    { title: "Make dinner" },
  ];

  // const[tasks, setTask] = useState(DUMMY_DATA);

  const taskListHandler = (totalTasks) => {
    setTask((prevTasks) => {
      return [totalTasks, ...prevTasks];
    });
  };

  return (
    <div>
      <h1>Hello</h1>
      <TaskForm onSaveTasksList={taskListHandler} />
      <ClearList></ClearList>
      <AllTasks items={tasks}></AllTasks>
    </div>
  );
};

export default App;
