import React from "react";
import TaskForm from "./components/TaskForm/TaskForm";
import TaskList from "./components/Tasklist/TaskList";
import ClearList from "./components/ClearList/ClearList";

const App = (props) => {
    const allTasks = [ 
        {title:"Feed The dog"},
        {title:"Buy the groceries"},
        {title:"Go to gym"},
        {title:"Make dinner"},
    ];
  const taskListHandler = (totalTasks) => {
    const tasks = {
      ...totalTasks,
    };
    console.log(tasks)
  };
  return (
    <div>
      <h1>Hello</h1>
      <TaskForm onSaveTasksList={taskListHandler} />
      <ClearList></ClearList>
      {allTasks.map((allTasks) => (<TaskList title={allTasks.title}/>))}
    //   <TaskList title={allTasks[0].title}/>
    //   <TaskList title={allTasks[1].title}/>
    //   <TaskList title={allTasks[2].title}/>
    //   <TaskList title={allTasks[3].title}/>
    </div>
  );
};

export default App;
